import React from 'react';
import { useEffect, useContext, useCallback } from 'react';
import { getAll, deleteVehicle, toggleFavorite } from '../../services/API.js';
import VehiclesCard from '../../components/VehiclesCard/index.js';
import SearchBar from '../../components/SearchBar/index.js';
import AppContext  from '../../context/AppContext.js';
import AddButton from '../../components/AddButton/index.js';

export default function Home() {
  const { data, setData } = useContext(AppContext);

  const fetchAllData = useCallback(async () => {
      const response = await getAll();
      setData(response)
  }, [setData]);


  useEffect(() => {
    fetchAllData();
  }, [fetchAllData]);

  const deleteBtn = async ( { target} ) => {
    const id = target.parentElement.parentElement.id;
    await deleteVehicle(id);
    fetchAllData();
  };

  const editBtn = async ( { target } ) => {
    const id = target.parentElement.parentElement.id;
    window.location.href = `/edit/${id}`;
  }

  const favoriteBtn = async ( { target } ) => {
    const id = target.parentElement.parentElement.id;
    await toggleFavorite(id);
  }

  return (

    <main>

        <SearchBar />
        <AddButton />

      <div> 
        { data.message ? 
        <h1>{data.message}</h1> 
        : 
        data.map(item => 
        <VehiclesCard
          editBtn={ editBtn }
          deleteBtn={ deleteBtn }
          id={item.id}
          key={item.id}
          title={`${item.brand} ${item.name}`}
          price={item.price}
          description={item.description}
          year={item.year}
          color={item.color}
        />)  }
      </div>
    </main>
  )
}
