import React, { useState, useEffect, useCallback } from 'react';
import { getById } from '../../services/API';
import { useParams } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import { updateVehicle } from '../../services/API';

export default function Edit() {

  const { id } = useParams();
  const [vehicle, setVehicle] = useState([]);

  const fetchData = useCallback(async () => {
    const response = await getById(id);
    setVehicle(response);
  }, [id]);

  const schema = yup.object({
    name: yup.string().max(50, 'Limite de 50 caracteres').required('É necessário que o campo de nome seja preenchido'),
    brand: yup.string().max(50, 'Limite de 50 caracteres').required('É necessário que o campo de marca seja preenchido'),
    color: yup.string().max(50, 'Limite de 50 caracteres').required('É necessário que o campo de cor seja preenchido'),
    year: yup.number().min(1000).max(2999).required('É necessário que o campo de ano seja preenchido'),
    plate: yup.string().min(7, 'A placa deve possuir 7 caracteres').max(7, 'A placa deve possuir 7 caracteres').required('É necessário que o campo de placa seja preenchido'),
    description: yup.string().max(500, 'Limite de 500 caracteres').required('É necessário que o campo de descrição seja preenchido'),
    price: yup.number().required('É necessário que o campo de preço seja preenchido'),
  }).required();

  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(schema)
  });

  const onSubmit = async (data) => {
    const structure = {
      name: data.name,
      brand: data.brand,
      color: data.color,
      year: data.year,
      plate: data.plate,
      description: data.description,
      price: data.price,
      favorite: vehicle[0].favorite,
    }
    await updateVehicle(structure, id);
    window.location.href = '/';
  };


  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return (
    <main>
      <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <p>Nome</p>
            <input defaultValue={ vehicle[0].name } {...register("name", { required: true, maxLength: 50 })} />
            <p>{errors.name?.message}</p>
          </div>
          <div>
            <p>Marca</p>
            <input defaultValue={ vehicle[0].brand } {...register("brand", { required: true, maxLength: 50 })} />
            <p>{errors.brand?.message}</p>
          </div>
          <div>
            <p>Cor</p>
            <input defaultValue={ vehicle[0].color } {...register("color", { required: true, maxLength: 50 })} />
            <p>{errors.color?.message}</p>
          </div>
          <div>
            <p>Ano</p>
            <input defaultValue={ vehicle[0].year } type="number" {...register("year", { required: true })} />
            <p>{errors.year && 'Insira um formato de ano válido'}</p>
          </div>
          <div>
            <p>Placa</p>
            <input defaultValue={ vehicle[0].plate } {...register("plate", { required: true, minLength:7, maxLength: 7 })} />
            <p>{errors.plate?.message}</p>
          </div>
          <div>
            <p>Descrição</p>
            <input defaultValue={ vehicle[0].description } {...register("description", { required: true })} />
            <p>{errors.description?.message}</p>
          </div>
          <div>
            <p>Preço</p>
            <input defaultValue={ vehicle[0].price } type="number" {...register("price", { required: true })} />
            <p>{errors.price && 'É necessário que o campo de preço seja preenchido corretamente'}</p>
          </div>
        <input type="submit" />
      </form>
    </main>
  )
}
