import React, { useState, useEffect } from 'react';
import { getAll } from '../../services/API';

export default function Filters() {

  const [brands, setBrands] = useState([]);
  const [colors, setColors] = useState([]);
  const [years, setYeats] = useState([]);

  const getFilters = async () => {
    const response = await getAll();
    setBrands(response.map((item) => item.brand));
    setColors(response.map((item) => item.color));
    setYeats(response.map((item) => item.year));
  }

  useEffect(() => {
    getFilters();
  }, []);

  const [brand, setBrand] = useState('');
  const [color, setColor] = useState('');
  const [year, setYear] = useState('');
  const [min, setMin] = useState('');
  const [max, setMax] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case 'brand':
        setBrand(value);
        break;
      case 'color':
        setColor(value);
        break;
      case 'year':
        setYear(value);
        break;
      case 'min':
        setMin(value);
        break;
      case 'max':
        setMax(value);
        break;
      default:
        break;
    }
  }

  const validateForm = () => {
    const minAndMaxNeedToBeAnumber = !isNaN(min) && !isNaN(max);
    const minAndMaxNeedToBeGreaterThanZero = min > 0 && max > 0;
    const minAndMaxNeedToBeGreaterThanMin = min > max;

  }

  return (
    <main>
      <form onSubmit={ validateForm }>
        <label>
          <select name="brand" value={ brand } onChange={ handleInputChange }>
            <option />
            { brands.map((e, i) => <option key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          <select name="color" value={ color } onChange={ handleInputChange } >
            <option />
            { colors.map((e, i) => <option key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          <select name="year" value={ year } onChange={ handleInputChange } >
            <option />
            { years.map((e, i) => <option key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          valor min
          <input onChange={ handleInputChange } value={ min } name="min" type="text" />
        </label>
        <label>
          valor max
          <input onChange={ handleInputChange } value={ max } name="max" type="text" />
        </label>
        <button type="submit">Salvar</button>
      </form>
    </main>
  )
}
