import React, { useState, useEffect } from 'react';
import { getAll } from '../../services/API';

export default function Filters() {

  const [brands, setBrands] = useState([]);
  const [colors, setColors] = useState([]);
  const [years, setYeats] = useState([]);

  const getFilters = async () => {
    const response = await getAll();
    setBrands(response.map((item) => item.brand));
    setColors(response.map((item) => item.color));
    setYeats(response.map((item) => item.year));
  }

  useEffect(() => {
    getFilters();
  }, []);

  const [brand, setBrand] = useState('');
  const [color, setColor] = useState('');
  const [year, setYear] = useState('');
  const [min, setMin] = useState('');
  const [max, setMax] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'brand') {
      setBrand(value);
    }
    if (name === 'color') {
      setColor(value);
    }
    if (name === 'year') {
      setYear(value);
    }
    if (name === 'min') {
      setMin(value);
    }
    if (name === 'max') {
      setMax(value);
    }
  }

  return (
    <main>
      <form>
        <label>
          <select value={ brand } onChange={ handleInputChange }>
            <option />
            { brands.map((e, i) => <option value={ e } name="brand" key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          <select value={ color } onChange={ handleInputChange } >
            <option />
            { colors.map((e, i) => <option name="color" key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          <select value={ year } onChange={ handleInputChange } >
            <option />
            { years.map((e, i) => <option name="year" key={ i }>{ e }</option>) }
          </select>
        </label>
        <label>
          valor min
          <input onChange={ handleInputChange } value={ min } name="min" type="text" />
        </label>
        <label>
          valor max
          <input onChange={ handleInputChange } value={ max } name="max" type="text" />
        </label>
        <button>Salvar</button>
      </form>
    </main>
  )
}
